package datatype;

/**
 * Holding the Integer value
 * 
 *
 */                                                                                           
public class Int {
	
	//properties
	public int value;
	
	//constuctor
	public Int(int val) {
		value=val;
	}
	@Override
	public String toString() {
		// TODO Auto-generated method stub
		return "" + value;
		
	}
}
