package homework_01;

public class Point {
	private float x;
	private float y;
	
	public  Point() {
		// TODO Auto-generated constructor stub
	}
	public  Point(float x, float y) {
		this.x = x;
		this.y = y;
	}
	public float getX() {
		return x;
	}

	public void setX(float x) {
		this.x = x;
	}

	public float getY() {
		return y;
	}

	public void setY(float y) {
		this.y = y;
	}


	}
	

	

